(() => {
  // javascript/bottom/carousels.js
  if (typeof Swiper !== "undefined") {
    new Swiper(".swiper-example", {
      slidesPerView: 3,
      spaceBetween: 30,
      loop: true,
      pagination: {
        el: ".swiper-pagination",
        clickable: true
      },
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev"
      }
    });
  }
})();
